import React from "react";
import ReactDOM from "react";
import Header from "./components/Header";
import Image from "./components/Image";
import logo from "./img/im1.png";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      helpText: "Help text",
      textChange: "",
    };
    this.inputClick = this.inputClick.bind(this);
  }
  componentDidUpdate(prevProp) {
    if (this.state.helpText !== "help") console.log("some");
  }
  render() {
    return (
      <div className="name">
        <Header aaaa="TEXT HEADER" />
        <button onClick={this.console}>Button</button>;
        <h1>{this.state.helpText}</h1>
        <h2>{this.state.textChange}</h2>
        <input
          placeholder={this.state.helpText}
          onChange={(event) =>
            this.setState({ textChange: event.target.value })
          }
          onClick={this.inputClick}
          onMouseEnter={this.mouseOver}
        />
        <p>{this.state.helpText === "Help text" ? "Yes" : "NO"}</p>
        <Image image={logo} />
      </div>
    );
  }
  inputClick() {
    this.setState({ helpText: "New header" });
    console.log("Clicked");
  }
  mouseOver() {
    console.log("Mouse Over");
  }
  console = () => {
    this.console.log("Button2");
  };
}

export default App;
